function safeSearchHandler(){const e=document.querySelector("input[name='safe-seach']");e.value=e.checked?"active":"off"}function submitHandler(){const e=document.querySelector("form[name='query-form']"),n=document.querySelector("input[name='query']").value,t=document.querySelector("input[name='page']").value;e.action="/query/"+n+"?page="+t}window.onload=function(){const e=document.querySelector("datalist#terms");fetch("../json/terms.json").then((e=>e.json())).then((n=>{n.terms.forEach((n=>{const t=document.createElement("option");t.value=n.term,e.append(t)}))})).catch((e=>console.error(e)))};
